import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  const [enteredTitle, setEneteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  // const [userInput,setUserInput] = useState({
  //   enteredTitle:'',
  //   enteredAmount:'',
  //   enteredDate:''
  // });


  const titleChangeHandler = (event) => {
    setEneteredTitle(event.target.value);

    // setUserInput({
    //   ...userInput, //Here you need to update all values present in setUserInput so copied userInput object and // made changes in only enteredTitle
    //   enteredTitle : event.target.value,
    // })

    //If you use this approach, React will guarantee that the state snapshot it gives you herein this inner function, will always be the latest state snapshot,keeping all scheduled state updates in mind.
    // setUserInput((prevState)=>{
    //   return {
    //     ...prevState,
    //     enteredTitle : event.target.value,
    //   }
    // })
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    // setUserInput({
    //   ...useInput,
    //   enteredAmount : event.target.value,
    // })
  }

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
    // setUserInput({
    //   ...useInput,
    //   enteredDate : event.target.value,
    // })
  }

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title : enteredTitle,
      amount : enteredAmount,
      date: new Date(enteredDate)
    }
    console.log(expenseData);
    setEneteredTitle('')
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit" >Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
