import "./ExpenseItem.css";

//A Component in React is just a JavaScript function.

// A special kind of function special regarding what it returns
// that it does return this JSX code, but other than that,
// it's just a Java script function.

//can give any name to function but the convention is to give same name as file name
//here props is an object whose properties are defined in key value pair in App.js component
function ExpenseItem(props) {
    const month = props.date.toLocaleString('en-US',{month:'long'});
    const day = props.date.toLocaleString('en-US',{day:'2-digit'});

  return (
    <div className="expense-item">
      <div>
        <div>{month}</div>
        <div>Year</div>
        <div>{Date}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
    </div>
  );
}

// To use this component everywhere need to EXPORT the function
export default ExpenseItem;

